mapper.update:
UPDATE voucher
code = #{voucherRequest.code}, condition_value = #{voucherRequest.conditionValue}, discount_price = #{voucherRequest.discountPrice}, end_time = #{voucherRequest.endTime}, start_time = #{voucherRequest.startTime}, total_quantity = #{voucherRequest.totalQuantity}, used_quantity = #{voucherRequest.usedQuantity}, is_active = #{voucherRequest.isActive}, updated_at = now()
